mapper.select:
select articleno,userid ,subject	, content,  hit,regtime,reply
from qna
where articleno = #{articleno}

mapper.insert:
insert into qna(
subject, userid, content
) values (
#{subject}, #{userid}, #{content}
)

mapper.update:
update qna
set subject = #{subject},
userid = #{userid},
content = #{content}
where articleno = #{articleno}

update qna
set 
 reply = #{reply}
where articleno = #{articleno}

update qna
set reply = null

where articleno = #{articleno}
